const iconMenu = document.querySelector(".menu__icon");
const menuBody = document.querySelector(".menu__body");
const myPic = document.querySelector(".bio__image");
const myBtn = document.querySelector(".glow-on-hover");
const myCon = document.querySelector(".page");
if (iconMenu) {
  iconMenu.addEventListener("click", function (e) {
    document.body.classList.toggle("_lock");
    iconMenu.classList.toggle("_active");
    menuBody.classList.toggle("_active");
    myPic.classList.toggle("invisible");
    myBtn.classList.toggle("invisible");
  });
}

if (iconMenu) {
  iconMenu.addEventListener("click", function (e) {
    myCon.classList.toggle("_locky");
  });
}
